// TODO check for errors.
int main()
{
    int argc = 0;
    HANDLE hFile;
    DWORD dwFileLength, dwTemp;
    BYTE* lpFileBuffer, *lpNewBuffer;

    LPWSTR* pArgvW;

    pArgvW = CommandLineToArgvW(GetCommandLineW(), &argc);
    
    if (argc != 3)
    {
        printf("Usage: RealPE.exe <exe_path> <output_shellcode_path>");
        return 0;
    }

    // Read exe_path file.
    hFile = CreateFileW(pArgvW[1], GENERIC_READ, FILE_SHARE_READ, NULL, OPEN_EXISTING, FILE_ATTRIBUTE_NORMAL, NULL);
    
    dwFileLength = GetFileSize(hFile, NULL);

    lpFileBuffer = (BYTE*)HeapAlloc(GetProcessHeap(), 0, dwFileLength);
    
    ReadFile(hFile, lpFileBuffer, dwFileLength, &dwTemp, NULL);

    CloseHandle(hFile);


    // Detect if exe_path is x64 or x86 using OptionalHeader.Magic.

    // X64
    if (((PIMAGE_NT_HEADERS)(lpFileBuffer + ((PIMAGE_DOS_HEADER)lpFileBuffer)->e_lfanew))->OptionalHeader.Magic == IMAGE_NT_OPTIONAL_HDR64_MAGIC)
    {
        lpNewBuffer = (BYTE*)HeapAlloc(GetProcessHeap(), 0, dwFileLength + sizeof(reflective_shellcode_x64));

        CopyMemory(lpNewBuffer, reflective_shellcode_x64, sizeof(reflective_shellcode_x64));
        CopyMemory((lpNewBuffer + sizeof(reflective_shellcode_x64)), lpFileBuffer, dwFileLength);

        // Write the shellcode and then the exe_file.
        hFile = CreateFileW(pArgvW[2], GENERIC_WRITE, 0, NULL, CREATE_NEW, FILE_ATTRIBUTE_NORMAL, NULL);
        WriteFile(hFile, lpNewBuffer, dwFileLength + sizeof(reflective_shellcode_x64), &dwTemp, NULL);
        CloseHandle(hFile);
    }

    // X86
    else if (((PIMAGE_NT_HEADERS)(lpFileBuffer + ((PIMAGE_DOS_HEADER)lpFileBuffer)->e_lfanew))->OptionalHeader.Magic == IMAGE_NT_OPTIONAL_HDR32_MAGIC)
    {
        BYTE* lpNewBuffer = (BYTE*)HeapAlloc(GetProcessHeap(), 0, dwFileLength + sizeof(reflective_shellcode_x86));

        CopyMemory(lpNewBuffer, reflective_shellcode_x86, sizeof(reflective_shellcode_x86));
        CopyMemory((lpNewBuffer + sizeof(reflective_shellcode_x86)), lpFileBuffer, dwFileLength);

        // Write the shellcode and then the exe_file.
        hFile = CreateFileW(pArgvW[2], GENERIC_WRITE, 0, NULL, CREATE_NEW, FILE_ATTRIBUTE_NORMAL, NULL);
        WriteFile(hFile, lpNewBuffer, dwFileLength + sizeof(reflective_shellcode_x86), &dwTemp, NULL);
        CloseHandle(hFile);
    }

    else
    {
        printf("Unsupported PE magic\n");

    }

    LocalFree(pArgvW);

    return 0;
}




